/**
 * NodeRed node with support for interaction with BLEs
 *
 * @author <a href="mailto:carlos.pedrinaci@open.ac.uk">Carlos Pedrinaci</a> (KMi - The Open University)
 * based on the initial node by Charalampos Doukas http://blog.buildinginternetofthings.com/2013/10/12/using-node-red-to-scan-for-ble-devices/
 */
module.exports = function(RED) {
    
    
    // The main node definition - most things happen in here
    function XpdTrainerNode(n) {
        // Create a RED node
        RED.nodes.createNode(this,n);

        // Store local copies of the node configuration (as defined in the .html)
        this.data = n.data;
        this.name  = n.name;
        this.category = n.category;

        var node = this;



        node.on('input', function (msg) {

            if (node.data && node.category) {
                var json = JSON.parse(node.data);
                is_one = true;
                for(var k in json){
                    if (typeof(json[k]) == 'object'){
                        is_one = false;
                        msg["input"] = json[k];
                        var outObj = {};
                        outObj[node.category] = 1;
                        msg["output"] = outObj;
                        node.send(msg);
                    }
                    else{
                        is_one = true;
                    }
                }

                if(is_one){
                    console.log(json);
                    msg["input"] = json;
                    var outObj = {};
                    outObj[node.category] = 1;
                    msg["output"] = outObj;
                    node.send(msg);
                }

                
                
            }
        });

    
        node.on("close", function() {

        });

        function toArr(obj){
            var re={};
            (function f(o){
                for(var k in o)
                    if(typeof o[k]=='object'){
                       var k2=k.replace(/['"]/g,'');
                       /^\d+$/.test(k2)&&(re[k2]=o[k]),f(o[k]); 
                    }
            })(obj);
            return re;
        }
    }


    
    // Register the node by name. This must be called before overriding any of the
    // Node functions.
    RED.nodes.registerType("xpd-trainer",XpdTrainerNode);

}
